function generateShadesOfColor(baseColor, alphaCode, numShades, type) {
  const match = baseColor.match(/^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i);
  if (!match) {
    throw new Error(
      "Invalid color format. Please use hexadecimal color notation."
    );
  }

  const [, r, g, b] = match.map((hex) => parseInt(hex, 16));

  const shades = [];
  for (let i = 0; i < numShades; i++) {
    let newR, newG, newB;

    switch (type) {
      case "red":
        newR = Math.floor(r + (255 - r) * (i / numShades));
        newG = g;
        newB = b;
        break;
      case "green":
        newR = r;
        newG = Math.floor(g + (255 - g) * (i / numShades));
        newB = b;
        break;
      case "blue":
        newR = r;
        newG = g;
        newB = Math.floor(b + (255 - b) * (i / numShades));
        break;
      case "white":
        newR = Math.floor(r + (255 - r) * (i / numShades));
        newG = Math.floor(g + (255 - g) * (i / numShades));
        newB = Math.floor(b + (255 - b) * (i / numShades));
        break;
      case "black":
        newR = Math.floor(r * (1 - i / numShades));
        newG = Math.floor(g * (1 - i / numShades));
        newB = Math.floor(b * (1 - i / numShades));
        break;
      default:
        throw new Error(
          'Invalid type. Use "red", "green", "blue", "white", or "black".'
        );
    }

    const shade = `#${newR.toString(16).padStart(2, "0")}${newG
      .toString(16)
      .padStart(2, "0")}${newB.toString(16).padStart(2, "0")}${alphaCode}`;
    shades.push(shade);
  }

  return shades;
}

