    case 3:
      baseColor = `#${inputColor[0]}${inputColor[0]}${inputColor[1]}${inputColor[1]}${inputColor[2]}${inputColor[2]}`;
      break;
    case 4:
      baseColor = `#${inputColor[0]}${inputColor[0]}${inputColor[1]}${inputColor[1]}${inputColor[2]}${inputColor[2]}`;
      alphaCode = `${inputColor[3]}${inputColor[3]}`;
      break;
    case 6:
      baseColor = `#${inputColor}`;
      break;
    case 8:
      baseColor = `#${inputColor.slice(0, 6)}`;
      alphaCode = inputColor.slice(6);
      break;
    default:
      throw new Error(
        'Invalid type. Use "red", "green", "blue", "white", or "black".'
      );
  }

  return { baseColor, alphaCode };
}

